}
// you can add your own logic if FIT is 0 here, if you wish
else if (msg.fit === 0) {
    // node.warn("Zero FIT so exporting at half speed");
    msg.decision = "Curtailing exports by 50% (0c FIT)";
    msg.decisions.push(msg.decision)
    msg.curtailment_percentage = 50;
    flow.set("curtailment", 50);
    return [null, msg, null]
}
else {
    // node.warn("Exporting ok - " + msg.fit +"c FIT");
    msg.decision = "OK to export (+ve FIT)";
