    msg.curtailment_percentage = 0;
    flow.set("curtailment", 0);
    return [null, null, msg]
}

let today = new Date()
//node.warn(msg.net_remaining);

// if FIT 0 or less, charge the battery at full speed
if (msg.fit <= 0 ) {
    //node.warn(msg.net_remaining);
    //node.warn("No FIT benefit - " + msg.fit + "c - so charging at full speed");
    msg.decision = "No feed-in benefit so not limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return [msg, null]
}
// there is some feed in benefit, work out if worth slow-charging for
else if (msg.battery < 100 && msg.net_remaining < 1) {
    //node.warn(msg.net_remaining);
    //node.warn("FIT benefit - " + msg.fit + "c but less than 1kWh remaining generation so prioritising battery");
    msg.decision = "Feed-in benefit but low generation remaining so not limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return [null, msg]
}
else if (msg.battery >= 30 && msg.net_remaining > msg.charge_required && today.getHours() > 12 && today.getHours() < 17) {
    //node.warn(msg.net_remaining);
    //node.warn("FIT benefit - " + msg.fit + "c - so slow charging");
    msg.decision = "Getting late in the day, FIT is probably going to be higher later so not limiting charge rate now";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return [null, msg]
}
else if (msg.battery >= 20 && msg.net_remaining > msg.charge_required) {
    //node.warn(msg.net_remaining);
    //node.warn("FIT benefit - " + msg.fit + "c - so slow charging");
    msg.decision = "Enough generation remaining so limiting charge rate to 1kW, better to get some FIT";
    msg.decisions.push(msg.decision)
    msg.battery_w = 1000;
    return [null, msg]
}
else if (msg.fit > 0.01 && msg.battery <= 15 && msg.net_remaining > msg.charge_required) {
    //node.warn(msg.net_remaining);
    //node.warn("FIT benefit but low battery - " + msg.fit + "c - so slow charging");
    msg.decision = "Enough generation remaining but low battery setting charge rate to 10% of PV";
    msg.decisions.push(msg.decision)
    let slow_charge = Math.round((msg.dc_power_now * .1)/100) * 100;
    msg.battery_w = slow_charge > 100 ? slow_charge : 100;
    return [null, msg]
}
//if the battery is almost full, you can customise the logic here
else if (msg.battery > 95 && msg.expected_generation_remaining > msg.charge_required && msg.net_remaining > msg.charge_required) {
    //node.warn(msg.net_remaining);
    //node.warn("FIT benefit and battery almost full - " + msg.fit + "c - so slow charging");
    msg.decision = "Enough generation remaining and battery almost full so setting charge rate to 10% of PV";
    msg.decisions.push(msg.decision)
    let slow_charge = Math.round((msg.dc_power_now * .1)/100) * 100;
    msg.battery_w = slow_charge > 100 ? slow_charge : 100;
    return [null, msg]
}
//there is some feed-in benefit but we are favouring battery charging
else {
    //node.warn(msg.net_remaining);
    //node.warn("Else - " + msg.fit + "c - so fast charging");
    msg.battery_w = 10000;
    msg.decision = "Feed-in benefit but not limiting charge rate";
    msg.decisions.push(msg.decision)
    return [msg, null]
}

