let today = new Date();
let current_hour = today.getHours();
msg.decisions = []
msg.expected_consumption_remaining = 0;
msg.expected_generation_remaining = 0;
let consumption_minus_generating_remaining = 0;
for (let hour in baseline_consumption) {
    //if (Number(hour) >= current_hour) {   // <- original, but this takes all day to midnight, not just until sun goes down
    // This may be appropriate if the battery is large enough to take you completely through the night, as you need to charge the battery up.
    // However, if the battery is limited in capacity, then don't draw from grid to charge to 100% if there will be remaining solar to charge it to 100% anyway.
    // If it is, then comment the below line, and uncomment line 10 above.
    if (Number(hour) >= current_hour && Number(hour) <= 17) {
        let consumption_this_hour = (baseline_consumption[hour] / 1000);
        msg.expected_consumption_remaining += consumption_this_hour;
        if (remaining_generation.hasOwnProperty(hour) && remaining_generation[hour]) {
            msg.expected_generation_remaining += Number(remaining_generation[hour]);
            consumption_minus_generating_remaining += Number(remaining_generation[hour]) - consumption_this_hour;
        }
    }
}
// node.warn(`baseline consumption remaining ${msg.expected_consumption_remaining.toPrecision(2)}kWh`);
// node.warn(`generation remaining ${msg.expected_generation_remaining.toPrecision(2)}kWh`)
msg.net_remaining = (msg.expected_generation_remaining - msg.expected_consumption_remaining);
// node.warn(`generation - consumption estimate ${msg.net_remaining.toPrecision(2)}kWh`);
