for (let i=0; i<forecasts.length-4; i++) {
    msg.grid_forecasts.push(forecasts[i].costsFlexUp);
    msg.fit_forecasts.push(forecasts[i].earningsFlexUp);
    if (msg.cheapest_grid > forecasts[i].costsFlexUp) {
        msg.cheapest_grid_time = forecasts[i].intervalStart;      
        msg.cheapest_grid = forecasts[i].costsFlexUp;
    }
    if (msg.best_fit < forecasts[i].earningsFlexUp) {
        msg.best_fit_time = forecasts[i].intervalStart;
        msg.best_fit = forecasts[i].earningsFlexUp;
    }
    msg.time_blocks_grid[forecasts[i].intervalStart] = 0
    msg.time_blocks_fit[forecasts[i].intervalStart] = 0
    for (let j=0; j<4; j++) {
        msg.time_blocks_grid[forecasts[i].intervalStart] += forecasts[i+j].costsFlexUp
        msg.time_blocks_fit[forecasts[i].intervalStart] += forecasts[i+j].earningsFlexUp
    }
    msg.time_blocks_grid[forecasts[i].intervalStart] = Number((msg.time_blocks_grid[forecasts[i].intervalStart]/6).toPrecision(2))
    msg.time_blocks_fit[forecasts[i].intervalStart] = Number((msg.time_blocks_fit[forecasts[i].intervalStart]/6).toPrecision(2))

    //node.warn("time_blocks_grid: " + msg.time_blocks_grid[forecasts[i].intervalStart]);
}
let cheapest_grid_block = 1000

for (let block in msg.time_blocks_grid) {
    //node.warn("time_blocks_grid: " + block);
    let [hour,minute] = block.split('T')[1].split("+")[0].split(":").slice(0, 2)
    if (Number(hour) < 18 && msg.time_blocks_grid[block] < cheapest_grid_block) {
        msg.cheapest_grid_start = [Number(hour),Number(minute)];
        cheapest_grid_block = msg.time_blocks_grid[block]
    }
}
