    msg.decisions.push(msg.decision)
    return [null,null,null,msg]
}

// Charge up to 40% during the night if the price is cheapish (30c or less, roughly, from HASS slider) and upcoming sell price likely to be 60c or greater (from HASS), to get ready for potential morning shoulder higher feed-in tarrif
if (today.getHours() >= 4 && today.getHours() < 5 && msg.current <= grid_buy_price_night_spike_prep && msg.battery < 40 && msg.maxsell4hrs > grid_sell_price_night) {
    msg.decision = "Opportunity to charge battery to 40% for shoulder period, battery is at" + msg.battery + "% and price is " + msg.current
    msg.decisions.push(msg.decision)
