if (msg.fit < 0) {
    // node.warn("Negative FIT so not exporting - " + msg.fit + "c FIT");
    msg.decision = "Curtailing exports (-ve FIT)";
    msg.decisions.push(msg.decision)
    msg.curtailment_percentage = 100;
    flow.set("curtailment", 100);
    return [msg, null, null]
}
// you can add your own logic if FIT is 0 here, if you wish
else if (msg.fit === 0) {
    // node.warn("Zero FIT so exporting at half speed");
    msg.decision = "Curtailing exports by 50% (0c FIT)";
    msg.decisions.push(msg.decision)
    msg.curtailment_percentage = 50;
    flow.set("curtailment", 50);
    return [null, msg, null]
}
else {
    // node.warn("Exporting ok - " + msg.fit +"c FIT");
    msg.decision = "OK to export (+ve FIT)";
    msg.decisions.push(msg.decision)
    msg.curtailment_percentage = 0;
    flow.set("curtailment", 0);
    return [null, null, msg]
}

let today = new Date();
// node.warn("hour now is " + today.getHours());

msg.baseline_consumption = flow.get("baseline_consumption");
// node.warn("baseline consumption=" + msg.baseline_consumption)

if (msg.fit >= .3 && msg.battery > 8 && today.getHours() >= 6 && today.getHours() < 8) {
    // node.warn("Morning peak occurring, battery at " + msg.battery + "%, between 6-8am, FIT " + msg.fit);
    msg.decision = "Morning peak occurring (>=30c,>8%), battery at " + msg.battery + "%, between 6-8am, FIT " + msg.fit;
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit > 1 && msg.battery > 40) {
    // node.warn("Extremely high FIT and limited capacity: " + msg.fit);
    msg.decision = "Extremely high FIT (>=$1,>40%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .6 && msg.battery > 50) {
    // node.warn("Very high FIT and some capacity: " + msg.fit);
    msg.decision = "Very high FIT (>=65c,>50%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .5 && msg.battery > 60) {
    // node.warn("Great FIT and available capacity: " + msg.fit);
    msg.decision = "High FIT (>=50c,>60%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .4 && msg.battery > 70) {
    // node.warn("Reasonable FIT and available capacity: "+msg.fit);
    msg.decision = "Moderate FIT (>=40c,>70%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .3 && msg.battery > 80) {
    // node.warn("Tolerable FIT and available capacity: " + msg.fit);
    msg.decision = "Acceptable FIT (>=32c,>80%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
//else if (msg.fit >= .30 && msg.battery > 80) {
//    // node.warn("Tolerable FIT and available capacity: " + msg.fit);
//    msg.decision = "Acceptable FIT and available capacity, so forcing discharge";
//    msg.decisions.push(msg.decision)
//    return [msg, null]
//}
else {
    // node.warn("Not worth exporting from battery: " + msg.fit);
    msg.decision = "Not worth exporting from battery.";
    msg.decisions.push(msg.decision)
    return [null,msg]
}

let today = new Date()

// if FIT 0 or less, charge the battery at full speed
if (msg.fit <= 0 ) {   // original
//if (msg.fit <= 0 && msg.importprice <= 0) {
    // node.warn("No FIT benefit - " + msg.fit + "c - so charging at full speed");
    msg.decision = "No feed-in benefit so not limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return [msg, null]
}
// there is some feed in benefit, work out if worth slow-charging for
else if (msg.battery < 100 && msg.net_remaining < 1) {
    // node.warn("FIT benefit - " + msg.fit + "c but less than 1kWh remaining generation so prioritising battery");
    msg.decision = "Feed-in benefit but low generation remaining so not limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return [null, msg]
}
else if (msg.battery >= 30 && msg.net_remaining > msg.charge_required && today.getHours() > 12 && today.getHours() < 17) {
    // node.warn("FIT benefit - " + msg.fit + "c - so slow charging");
    msg.decision = "Getting late in the day, FIT is probably going to be higher later so not limiting charge rate now";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return [null, msg]
}
else if (msg.battery >= 20 && msg.net_remaining > msg.charge_required) {
    // node.warn("FIT benefit - " + msg.fit + "c - so slow charging");
    msg.decision = "Enough generation remaining so limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 1000;
    return [null, msg]
}
else if (msg.battery <= 15 && msg.net_remaining > msg.charge_required) {
    // node.warn("FIT benefit but low battery - " + msg.fit + "c - so slow charging");
    msg.decision = "Enough generation remaining but low battery setting charge rate to 10% of PV";
    msg.decisions.push(msg.decision)
    let slow_charge = Math.round((msg.dc_power_now * .1)/100) * 100;
    msg.battery_w = slow_charge > 100 ? slow_charge : 100;
    return [null, msg]
}
//if the battery is almost full, you can customise the logic here
else if (msg.battery > 95 && msg.expected_generation_remaining > msg.charge_required && msg.net_remaining > msg.charge_required) {
    // node.warn("FIT benefit and battery almost full - " + msg.fit + "c - so slow charging");
    msg.decision = "Enough generation remaining and battery almost full so setting charge rate to 10% of PV";
    msg.decisions.push(msg.decision)
    let slow_charge = Math.round((msg.dc_power_now * .1)/100) * 100;
    msg.battery_w = slow_charge > 100 ? slow_charge : 100;
    return [null, msg]
}
//there is some feed-in benefit but we are favouring battery charging
else {
    // node.warn("Else - " + msg.fit + "c - so fast charging");
    msg.battery_w = 5000;
    msg.decision = "Feed-in benefit but not limiting charge rate";
    msg.decisions.push(msg.decision)
    return [msg, null]
}

