msg.cheapest_grid_start = undefined
msg.best_fit_start = undefined

msg.time_blocks_grid = {}
msg.time_blocks_fit = {}
msg.cheapest_grid = 1000
msg.best_fit = -1000
msg.cheapest_grid_time = undefined
msg.best_fit_time = undefined

let grid_buy_price_day = msg.grid_buy_price_day / 100;
let grid_buy_price_night_spike_prep = msg.grid_buy_price_night_spike_prep / 100;
let grid_sell_price_day = msg.grid_sell_price_day / 100;
let grid_sell_price_night = msg.grid_sell_price_night / 100;
let grid_buy_price_day_spike_prep = msg.grid_buy_price_day_spike_prep / 100;

let minbuy4hrs = msg.minbuy4hrs;

for (let i=0; i<forecasts.length-4; i++) {
    msg.grid_forecasts.push(forecasts[i].costsFlexUp);
    msg.fit_forecasts.push(forecasts[i].earningsFlexUp);
    if (msg.cheapest_grid > forecasts[i].costsFlexUp) {
        msg.cheapest_grid_time = forecasts[i].intervalStart;      
        msg.cheapest_grid = forecasts[i].costsFlexUp;
    }
    if (msg.best_fit < forecasts[i].earningsFlexUp) {
        msg.best_fit_time = forecasts[i].intervalStart;
        msg.best_fit = forecasts[i].earningsFlexUp;
    }
    msg.time_blocks_grid[forecasts[i].intervalStart] = 0
    msg.time_blocks_fit[forecasts[i].intervalStart] = 0
    for (let j=0; j<4; j++) {
        msg.time_blocks_grid[forecasts[i].intervalStart] += forecasts[i+j].costsFlexUp
        msg.time_blocks_fit[forecasts[i].intervalStart] += forecasts[i+j].earningsFlexUp
    }
    msg.time_blocks_grid[forecasts[i].intervalStart] = Number((msg.time_blocks_grid[forecasts[i].intervalStart]/6).toPrecision(2))
    msg.time_blocks_fit[forecasts[i].intervalStart] = Number((msg.time_blocks_fit[forecasts[i].intervalStart]/6).toPrecision(2))

    //node.warn("time_blocks_grid: " + msg.time_blocks_grid[forecasts[i].intervalStart]);
}
let cheapest_grid_block = 1000

for (let block in msg.time_blocks_grid) {
    //node.warn("time_blocks_grid: " + block);
    let [hour,minute] = block.split('T')[1].split("+")[0].split(":").slice(0, 2)
    if (Number(hour) < 18 && msg.time_blocks_grid[block] < cheapest_grid_block) {
        msg.cheapest_grid_start = [Number(hour),Number(minute)];
        cheapest_grid_block = msg.time_blocks_grid[block]
    }
    else {
        // node.warn(cheapest_grid_block + " is cheaper than or same as " + msg.time_blocks_grid[block])
    }
}
let best_fit_block = -1000
for (let block in msg.time_blocks_fit) {
    let [hour,minute] = block.split('T')[1].split("+")[0].split(":").slice(0, 2)
    if (Number(hour) < 18 && msg.time_blocks_fit[block] > best_fit_block) {
        msg.best_fit_start = [Number(hour),Number(minute)];
        best_fit_block = msg.time_blocks_fit[block]
    }
}

// Charge up to 40% during the night if the price is cheapish (30c or less, roughly, from HASS slider) and upcoming sell price likely to be 60c or greater (from HASS), to get ready for potential morning shoulder higher feed-in tarrif
if (today.getHours() >= 4 && today.getHours() < 5 && msg.current <= grid_buy_price_night_spike_prep && msg.battery < 40 && msg.maxsell4hrs > grid_sell_price_night) {
    msg.decision = "Opportunity to charge battery to 40% for shoulder period, battery is at" + msg.battery + "% and price is " + msg.current
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}

// node.warn("cheapest grid starting at "+msg.cheapest_grid_start.join(":"));
// node.warn(today.getHours() >= Number(msg.cheapcheapest_grid_startest_grid_start[0]))
// node.warn(today.getMinutes() >= Number(msg.cheapest_grid_start[1]))
// node.warn(msg)
// less than or equal to 0 decisions
if (msg.current <= 0) {
    //if (msg.battery <= 98) {
    //if (msg.battery <= 98 && (today.getHours() <16 || today.getHours() > 21)) {
        // added next line to make sure that only buy when it is the cheapest part of the next 4 hrs (within 10% of the cheapest)
    if (msg.battery <= 98 && msg.current <= (minbuy4hrs * .9) && (today.getHours() <16 || today.getHours() > 21)) {
        // node.warn("FREE ELECTRICITY and battery <= 98");
        msg.decision = "Free electricity from grid, cheap part of next 4 hrs, so fast charging";
        msg.decisions.push(msg.decision)
        return [msg,null,null]
    }
    //shut down inverter
    //else if (msg.current < -.03 && msg.forecast < 0) {
    else if (msg.current < -.03 && msg.forecast < 0 && (today.getHours() < 16 || today.getHours() > 21)) {
        // node.warn("Being paid more than 3c to use from grid, and forecast is negative too");
        msg.decision = "Being paid more than 3c to use from grid, so shutting down inverter";
        msg.decisions.push(msg.decision)
        return [null, null, msg]
    }
}

// Check if battery is less than 50% charged at 3pm.
// We need to prepare for the demand period at 6pm so get enough charge in the battery to get us through that peak.
else if (today.getHours() >= 14 && today.getHours() < 16 && msg.current <= grid_buy_price_day && msg.battery < 90) {
    // If battery is under 90%, charge it up if the price is under roughly 25c/kWh (from HASS)
    // node.warn("Need to charge battery to prepare for demand period since the battery is only at" + msg.battery + "%")
    msg.decision = "Need to charge battery to 90% to prepare for upcomging demand period since the battery is only at" + msg.battery +"%"
    msg.decisions.push(msg.decision)
    return [msg,null,null]
}

// Check if battery is less than 100% charged at 3pm and whether there is a major spike coming (definition, > $2/kWh sell price).
// We need to prepare for this spike period, if true, so fully charge battery.
// Will only charge if current price is 40c/kWh or below (defined in HASS)
else if (today.getHours() >= 15 && today.getHours() < 16 && msg.current <= grid_buy_price_day_spike_prep && msg.battery < 100 && msg.maxsell4hrs > grid_sell_price_day) {
    // If battery is not fully charged, charge it up if the price is under roughly 40c/kWh (from HASS) because there is going to be a sell price of over roughly $2/kwh (from HASS) within the next 4 hours
    // node.warn("Need to charge battery to prepare for demand period since the battery is only at" + msg.battery + "%")
    msg.decision = "Need to charge battery to 100% to prepare for upcoming spike since the battery is only at" + msg.battery + "% and sell price expected to be up to $" + msg.maxsell4hrs;
    msg.decisions.push(msg.decision)
    return [msg,null,null]
}

//very cheap power decisions
//else if (msg.current <= .04 && msg.battery < 75 && today.getHours() > 12) {
else if (msg.current <= .04 && msg.battery < 75 && today.getHours() > 12 && (today.getHours() < 16 || today.getHours() >= 21)) {
    // node.warn("very cheap charging and battery low for afternoon")
    msg.decision = "After 1pm, very cheap grid, battery low, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}
else if (msg.current <= .04 && msg.battery < 30 && today.getHours() < 8) {
    // node.warn("very cheap charging and battery low for morning")
    msg.decision = "Before 7am, very cheap grid, battery low, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}
else if (msg.current < grid_buy_price_day && msg.battery < 100 && msg.net_remaining <= 0 && today.getHours() > 10 && today.getHours() < 16) {
    // node.warn("Little generation left and price is cheap so force charging");
    // grid_buy_price_day is taken from HASS slider
    msg.decision = "Between 10am and 4pm, < " + grid_buy_price_day  + ", battery < 100, low generation remaining, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}
// else if (msg.current <= .15 && msg.battery < 85 && msg.net_remaining < 1 && today.getHours() > 14 && today.getHours() < 16) {
//     // node.warn("Little generation left and price is cheap so force charging");
//     msg.decision = "Between 2pm and 4pm, <= 15c grid, battery <85, low generation remaining, so force-charging";
//     msg.decisions.push(msg.decision)
//     return [msg, null, null]1
// }
// msg.current < .10 && msg.battery < 100 && msg.net_remaining < 2 && 
// grid_buy_price_day is taken from HASS slider
else if (msg.current < grid_buy_price_day && msg.battery < 100 && msg.net_remaining < 1 && today.getHours() >= Number(msg.cheapest_grid_start[0]) && today.getHours() < (Number(msg.cheapest_grid_start[0])+3) && today.getMinutes() >= Number(msg.cheapest_grid_start[1])) {
    if(today.getHours() > 8 && today.getHours() < 16) {
    // node.warn("Not enough generation left to fully charge battery so force charging");
    msg.decision = "In cheapest grid period, generation remaining not enough to charge battery, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
    }
}

else {
    // node.warn("Not worth charging battery from grid, not worth shutting down inverter");
    msg.decision = "Not worth charging battery from grid, not worth shutting down inverter";
    msg.decisions.push(msg.decision)
    return [null, msg, null]
}

let forecasts = msg.forecast_obj.attributes.forecast;

let sellprice = null;
let maxsellprice = null;
let minsellprice = null;

// 48 = 4hrs of 5min intervals

// temp
maxsellprice = forecasts[0].earningsFlexUp;
minsellprice = forecasts[0].earningsFlexUp;

for (let i = 1; i <= 47; i++) {
    sellprice = forecasts[i].earningsFlexUp;

    if (sellprice > maxsellprice) {
        maxsellprice = sellprice;
    }

    if (sellprice < minsellprice) {
        minsellprice = sellprice;
    }
}

msg.maxsell4hrs = maxsellprice;
msg.minsell4hrs = minsellprice;

return msg;

let remaining_generation = flow.get("remaining_today");
let baseline_consumption = flow.get("baseline_consumption");

let battery_charge = msg.battery_charge;
let battery_charge_required = null;
let battery_capacity = 19.2;
let battery_level = msg.battery_level;

let battery_full_hour = null;

let today = new Date();
let current_hour = today.getHours();
let sundown_hour = 17;

msg.expected_consumption_remaining = 0;
msg.expected_generation_remaining = 0;

battery_charge_required = battery_capacity - battery_charge;

let consumption_minus_generating_remaining = 0;

for (let hour in baseline_consumption) {
    if (Number(hour) >= current_hour && Number(hour) < sundown_hour) {
        let consumption_this_hour = (baseline_consumption[hour] / 1000);
        msg.expected_consumption_remaining += consumption_this_hour;
        if (remaining_generation.hasOwnProperty(hour) && remaining_generation[hour]) {
            msg.expected_generation_remaining += Number(remaining_generation[hour]);
            consumption_minus_generating_remaining += Number(remaining_generation[hour]) - consumption_this_hour;

            // Work out what hour we will have a 100% battery
            battery_charge_required = battery_charge_required - remaining_generation[hour];

            if(battery_charge_required <= 0)
                {
                battery_full_hour = Number(hour) + 1;
                break;
                }
        }
    }
    else
    {
        battery_full_hour = 99;
    }
}

// This is a bit of a mess, need to work on this to get it better (won't be hard, this is quick and dirty)
switch (battery_full_hour) {
    case 10:
        msg.battery_full_hour = "10am";
        break;
    case 11:
        msg.battery_full_hour = "11am";
        break;
    case 12:
        msg.battery_full_hour = "12pm";
        break;
    case 13:
        msg.battery_full_hour = "1pm";
        break;
    case 14:
        msg.battery_full_hour = "2pm";
        break;
    case 15:
        msg.battery_full_hour = "3pm";
        break;
    case 16:
        msg.battery_full_hour = "4pm";
        break;
    case 17:
        msg.battery_full_hour = "5pm";
        break;
    case 99:
        msg.battery_full_hour = "99";
        break;
    default:
        msg.battery_full_hour = "Full";
}

msg.payload = {
    data: {
        state: msg.battery_full_hour,
        entity_id: `sensor.battery_hour_full`,
        attributes: {
            'name': 'battery_hour_full',
            'entries': [
                {'title': 'BATTERY_HOUR_FULL', battery_full_hour}
            ],
            friendly_name: 'Battery hour when full',
            icon: 'mdi:battery-high'
        }
    }
};

return [{payload: msg.payload, entity_id: 'sensor.battery_hour_full'}];

let forecasts = msg.forecast_obj.attributes.forecast;
let buyprice = null;
let minbuyprice = null;
let maxbuyprice = null;

maxbuyprice = forecasts[0].costsFlexUp;
minbuyprice = forecasts[0].costsFlexUp;

// 48 = 4hrs of 5min intervals
for (let i = 1; i <= 47; i++) {
    buyprice = forecasts[i].costsFlexUp;

    if (buyprice < minbuyprice) {
        minbuyprice = buyprice;
    }
    if (buyprice > maxbuyprice) {
        maxbuyprice = buyprice;
    }
}

msg.minbuy4hrs = minbuyprice;
msg.maxbuy4hrs = maxbuyprice;

return msg;

let lowestbuy4hrs = msg.minbuy4hrs;
let highestbuy4hrs = msg.maxbuy4hrs;

msg.payloadlow = {
    data: {
        state: msg.minbuy4hrs,
        entity_id: `sensor.lowestbuy4hrs`,
        attributes: {
            'name': 'lowest_buy_4hrs',
            'entries': [
                {'title': 'LOWEST_BUY_4HRS', lowestbuy4hrs}
            ],
            friendly_name: 'Lowest buy price in next 4hrs',
            icon: 'mdi:currency-usd'
        }
    }
}

msg.payloadhigh = {
    data: {
        state: msg.maxbuy4hrs,
        entity_id: `sensor.highestbuy4hrs`,
        attributes: {
            'name': 'highest_buy_4hrs',
            'entries': [
                { 'title': 'HIGHEST_BUY_4HRS', highestbuy4hrs }
            ],
            friendly_name: 'Highest buy price in next 4hrs',
            icon: 'mdi:currency-usd'
        }
    }
}

return [
    {payload: msg.payloadlow, entity_id: 'sensor.lowestbuy4hrs'},
    {payload: msg.payloadhigh, entity_id: 'sensor.highestbuy4hrs'}
    ];

let highestsell4hrs = msg.maxsell4hrs
let lowestsell4hrs = msg.minsell4hrs

msg.payloadhigh = {
    data: {
        state: msg.maxsell4hrs,
        entity_id: `sensor.highestsell4hrs`,
        attributes: {
            'name': 'highest_sell_4hrs',
            'entries': [
                {'title': 'HIGHEST_SELL_4HRS', highestsell4hrs}
            ],
            friendly_name: 'Highest sell price in next 4hrs',
            icon: 'mdi:currency-usd'
        }
    }
}

msg.payloadlow = {
    data: {
        state: msg.minsell4hrs,
        entity_id: `sensor.lowestsell4hrs`,
        attributes: {
            'name': 'lowest_sell_4hrs',
            'entries': [
                {'title': 'LOWEST_SELL_4HRS', lowestsell4hrs}
            ],
            friendly_name: 'Lowest sell price in next 4hrs',
            icon: 'mdi:currency-usd'
        }
    }
}

return [
    {payload: msg.payloadhigh, entity_id: 'sensor.highestsell4hrs'},
    {payload: msg.payloadlow, entity_id: 'sensor.lowestsell4hrs'}
];
