if (msg.fit < 0) {
    // node.warn("Negative FIT so not exporting - " + msg.fit + "c FIT");
    msg.decision = "Curtailing exports (-ve FIT)";
    msg.decisions.push(msg.decision)
    msg.curtailment_percentage = 100;
    flow.set("curtailment", 100);
    return [msg, null]
}
else {
    // node.warn("Exporting ok - " + msg.fit +"c FIT");
    msg.decision = "OK to export (+ve FIT)";
    msg.decisions.push(msg.decision)
    msg.curtailment_percentage = 0;
    flow.set("curtailment", 0);
    return [null, msg]
}

