msg.baseline_consumption = flow.get("baseline_consumption");
// node.warn("baseline consumption=" + msg.baseline_consumption)

if (msg.fit >= .3 && msg.battery > 8 && today.getHours() >= 6 && today.getHours() < 8) {
    // node.warn("Morning peak occurring, battery at " + msg.battery + "%, between 6-8am, FIT " + msg.fit);
    msg.decision = "Morning peak occurring (>=30c,>8%), battery at " + msg.battery + "%, between 6-8am, FIT " + msg.fit;
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit > 1 && msg.battery > 40) {
    // node.warn("Extremely high FIT and limited capacity: " + msg.fit);
    msg.decision = "Extremely high FIT (>=$1,>40%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .6 && msg.battery > 50) {
    // node.warn("Very high FIT and some capacity: " + msg.fit);
    msg.decision = "Very high FIT (>=65c,>50%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .5 && msg.battery > 60) {
    // node.warn("Great FIT and available capacity: " + msg.fit);
    msg.decision = "High FIT (>=50c,>60%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .4 && msg.battery > 70) {
    // node.warn("Reasonable FIT and available capacity: "+msg.fit);
    msg.decision = "Moderate FIT (>=40c,>70%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else if (msg.fit >= .3 && msg.battery > 80) {
    // node.warn("Tolerable FIT and available capacity: " + msg.fit);
    msg.decision = "Acceptable FIT (>=32c,>80%), so forcing discharge";
    msg.decisions.push(msg.decision)
    return [msg, null]
}
else {
    // node.warn("Not worth exporting from battery: " + msg.fit);
    msg.decision = "Not worth exporting from battery.";
    msg.decisions.push(msg.decision)
    return [null,msg]
}

let today = new Date()

// if FIT 0 or less, allow the battery to be charged at full speed
if (msg.fit <= 0 ) {   
    // node.warn("No FIT benefit - " + msg.fit + "c - so charging at full speed");
    msg.decision = "No feed-in benefit so not limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return msg
}
else if (msg.battery >= 30 && today.getHours() > 12 && today.getHours() < 17) {
    // node.warn("FIT benefit - " + msg.fit + "c - so slow charging");
    msg.decision = "Getting late in the day, FIT is probably going to be higher later so not limiting charge rate now";
    msg.decisions.push(msg.decision)
    msg.battery_w = 10000;
    return msg
}
//there is some feed-in benefit but we are favouring battery charging
else {
    // node.warn("Else - " + msg.fit + "c - so fast charging");
    msg.decision = "Feed-in benefit but not limiting charge rate";
    msg.decisions.push(msg.decision)
    msg.battery_w = 5000;
    return msg
}

