// node.warn("cheapest grid starting at "+msg.cheapest_grid_start.join(":"));
// node.warn(today.getHours() >= Number(msg.cheapcheapest_grid_startest_grid_start[0]))
// node.warn(today.getMinutes() >= Number(msg.cheapest_grid_start[1]))
// node.warn(msg)
// less than or equal to 0 decisions
if (msg.current <= 0) {
    //if (msg.battery <= 98) {
    //if (msg.battery <= 98 && (today.getHours() <16 || today.getHours() > 21)) {
        // added next line to make sure that only buy when it is the cheapest part of the next 4 hrs (within 10% of the cheapest)
    if (msg.battery <= 98 && msg.current <= (minbuy4hrs * .9) && (today.getHours() <16 || today.getHours() > 21)) {
        // node.warn("FREE ELECTRICITY and battery <= 98");
        msg.decision = "Free electricity from grid, cheap part of next 4 hrs, so fast charging";
        msg.decisions.push(msg.decision)
        return [msg,null,null]
    }
    //shut down inverter
    //else if (msg.current < -.03 && msg.forecast < 0) {
    else if (msg.current < -.03 && msg.forecast < 0 && (today.getHours() < 16 || today.getHours() > 21)) {
        // node.warn("Being paid more than 3c to use from grid, and forecast is negative too");
        msg.decision = "Being paid more than 3c to use from grid, so shutting down inverter";
        msg.decisions.push(msg.decision)
        return [null, null, msg]
    }
}
// Check if battery is less than 50% charged at 3pm.
// We need to prepare for the demand period at 6pm so get enough charge in the battery to get us through that peak.
else if (today.getHours() >= 14 && today.getHours() < 16 && msg.current <= grid_buy_price_day && msg.battery < 90) {
    // If battery is under 90%, charge it up if the price is under roughly 25c/kWh (from HASS)
    // node.warn("Need to charge battery to prepare for demand period since the battery is only at" + msg.battery + "%")
    msg.decision = "Need to charge battery to 90% to prepare for upcomging demand period since the battery is only at" + msg.battery +"%"
    msg.decisions.push(msg.decision)
    return [msg,null,null]
}
// Check if battery is less than 100% charged at 3pm and whether there is a major spike coming (definition, > $2/kWh sell price).
// We need to prepare for this spike period, if true, so fully charge battery.
// Will only charge if current price is 40c/kWh or below (defined in HASS)
else if (today.getHours() >= 15 && today.getHours() < 16 && msg.current <= grid_buy_price_day_spike_prep && msg.battery < 100 && msg.maxsell4hrs > grid_sell_price_day) {
    // If battery is not fully charged, charge it up if the price is under roughly 40c/kWh (from HASS) because there is going to be a sell price of over roughly $2/kwh (from HASS) within the next 4 hours
    // node.warn("Need to charge battery to prepare for demand period since the battery is only at" + msg.battery + "%")
    msg.decision = "Need to charge battery to 100% to prepare for upcoming spike since the battery is only at" + msg.battery + "% and sell price expected to be up to $" + msg.maxsell4hrs;
    msg.decisions.push(msg.decision)
    return [msg,null,null]
}
//very cheap power decisions
//else if (msg.current <= .04 && msg.battery < 75 && today.getHours() > 12) {
else if (msg.current <= .04 && msg.battery < 75 && today.getHours() > 12 && (today.getHours() < 16 || today.getHours() >= 21)) {
    // node.warn("very cheap charging and battery low for afternoon")
    msg.decision = "After 1pm, very cheap grid, battery low, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}
else if (msg.current <= .04 && msg.battery < 30 && today.getHours() < 8) {
    // node.warn("very cheap charging and battery low for morning")
    msg.decision = "Before 7am, very cheap grid, battery low, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}
else if (msg.current < grid_buy_price_day && msg.battery < 100 && today.getHours() > 10 && today.getHours() < 16) {
    // node.warn("Little generation left and price is cheap so force charging");
    // grid_buy_price_day is taken from HASS slider
    msg.decision = "Between 10am and 4pm, < " + grid_buy_price_day  + ", battery < 100, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
}
else if (msg.current < grid_buy_price_day && msg.battery < 100 && today.getHours() >= Number(msg.cheapest_grid_start[0]) && today.getHours() < (Number(msg.cheapest_grid_start[0])+3) && today.getMinutes() >= Number(msg.cheapest_grid_start[1])) {
    if(today.getHours() > 8 && today.getHours() < 16) {
    // node.warn("Not enough generation left to fully charge battery so force charging");
    msg.decision = "In cheapest grid period, generation remaining not enough to charge battery, so force-charging";
    msg.decisions.push(msg.decision)
    return [msg, null, null]
    }
}
else {
    // node.warn("Not worth charging battery from grid, not worth shutting down inverter");
    msg.decision = "Not worth charging battery from grid, not worth shutting down inverter";
    msg.decisions.push(msg.decision)
    return [null, msg, null]
}

